mapper.select:
select * from photobook 
 where user_id=#{userId} 
 and photo_folder_name=#{photoFolderName}

select photo_folder_name from
photobook
where user_id=#{userId}
and photo_share_id1=#{photoShareId1}
or photo_share_id2=#{photoShareId1}
or photo_share_id3=#{photoShareId1}
or photo_share_id4=#{photoShareId1}

select * from photobook p
join users u on p.user_id=u.user_id
where
PHOTO_SHARE_ID1=#{userId}
or photo_share_id2=#{userId}
or
photo_share_id3=#{userId}
or photo_share_id4=#{userId}

mapper.update:
update photobook set
PHOTO_SHARE_ID1 = #{photoShareId1}
where PHOTO_FOLDER_NAME = #{photoFolderName}
and user_id=#{userId}

update photobook set
PHOTO_SHARE_ID2 = #{photoShareId2}
where PHOTO_FOLDER_NAME = #{photoFolderName}
and user_id=#{userId}

update photobook set
PHOTO_SHARE_ID3 = #{photoShareId3}
where PHOTO_FOLDER_NAME = #{photoFolderName}
and user_id=#{userId}

update photobook set
PHOTO_SHARE_ID4 = #{photoShareId4}
where PHOTO_FOLDER_NAME = #{photoFolderName}
and user_id=#{userId}
